import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { 
  Search, 
  Filter, 
  Edit, 
  Trash2, 
  Eye, 
  UserCheck,
  UserX,
  Mail,
  Phone,
  Calendar,
  Shield,
  CheckCircle,
  XCircle,
  Clock,
  Plus,
  AlertTriangle
} from 'lucide-react';
import Card from '../../components/ui/Card';
import Button from '../../components/ui/Button';
import Badge from '../../components/ui/Badge';
import UserForm from '../../components/admin/UserForm';
import { adminAPI } from '../../services/api';
import { useAdminAuth } from '../../components/admin/AdminAuth';

const UsersManagement = () => {
  const { isAuthenticated } = useAdminAuth();
  
  const [filters, setFilters] = useState({
    search: '',
    status: '',
    kyc_status: '',
    include_inactive: false,
    sort_by: 'created_at',
    sort_order: 'desc'
  });
  const [currentPage, setCurrentPage] = useState(1);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showUserForm, setShowUserForm] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);
  const [showKYCModal, setShowKYCModal] = useState(false);
  const [selectedUserKYC, setSelectedUserKYC] = useState(null);
  const [kycData, setKycData] = useState(null);
  const [loadingKYC, setLoadingKYC] = useState(false);

  const queryClient = useQueryClient();

  // Fetch users
  const { data: usersData, isLoading, error } = useQuery(
    ['admin-users', filters, currentPage],
    () => adminAPI.getUsers({
      ...filters,
      page: currentPage,
      limit: 10,
      include_inactive: filters.include_inactive
    }),
    {
      enabled: isAuthenticated
    }
  );

  // Fetch all KYC statuses
  const { data: allKYCData } = useQuery(
    ['all-kyc-statuses'],
    () => adminAPI.getAllKYC(),
    {
      enabled: isAuthenticated,
      staleTime: 60000 // Cache for 1 minute
    }
  );

  // Update user status mutation
  const updateStatusMutation = useMutation(
    ({ id, is_active }) => adminAPI.updateUserStatus(id, { is_active }),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['admin-users']);
        setShowModal(false);
        setSelectedUser(null);
      }
    }
  );

  // Create user mutation
  const createUserMutation = useMutation(
    (userData) => adminAPI.createUser(userData),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['admin-users']);
        setShowUserForm(false);
        setEditingUser(null);
      }
    }
  );

  // Update user mutation
  const updateUserMutation = useMutation(
    ({ id, data }) => adminAPI.updateUser(id, data),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['admin-users']);
        setShowUserForm(false);
        setEditingUser(null);
      }
    }
  );

  // Delete user mutation
  const deleteUserMutation = useMutation(
    (id) => adminAPI.deleteUser(id),
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['admin-users']);
        setShowDeleteModal(false);
        setUserToDelete(null);
        console.log('User deleted successfully');
      },
      onError: (error) => {
        console.error('Failed to delete user:', error);
        alert('Failed to delete user. Please try again.');
      }
    }
  );

  // Update KYC status mutation
  const updateKYCMutation = useMutation(
    ({ kycId, kycStatus, reviewer, rejectionReason }) => 
      adminAPI.updateKYCStatus(kycId, { status: kycStatus, reviewer, rejectionReason }),
    {
      onSuccess: (response, variables) => {
        queryClient.invalidateQueries(['admin-users']);
        queryClient.invalidateQueries(['all-kyc-statuses']); // Refresh KYC statuses
        setShowKYCModal(false);
        setSelectedUserKYC(null);
        setKycData(null);
        console.log('KYC status updated successfully:', response);
        alert(`✅ KYC status updated to "${variables.kycStatus}" successfully!`);
      },
      onError: (error) => {
        console.error('Failed to update KYC status:', error);
        console.error('Error details:', {
          message: error.message,
          response: error.response?.data,
          status: error.response?.status
        });
        
        let errorMessage = 'Failed to update KYC status.\n\n';
        
        if (error.message === 'Network Error') {
          errorMessage += '⚠️ CORS Error: The backend does not allow this request.\n\n';
          errorMessage += 'Backend team needs to:\n';
          errorMessage += '1. Enable CORS for PUT/PATCH methods\n';
          errorMessage += '2. Add http://localhost:3000 to allowed origins\n';
          errorMessage += '3. Add "PUT" and "PATCH" to Access-Control-Allow-Methods';
        } else if (error.response?.status === 404) {
          errorMessage += 'KYC record not found.';
        } else if (error.response?.status === 401) {
          errorMessage += 'Unauthorized. Admin access required.';
        } else {
          errorMessage += error.response?.data?.message || error.message;
        }
        
        alert(errorMessage);
      }
    }
  );

  // Handle different response formats from backend
  const users = usersData?.data?.data?.users || 
                usersData?.data?.users || 
                usersData?.data || 
                (Array.isArray(usersData) ? usersData : []);
  const pagination = usersData?.data?.data?.pagination || 
                     usersData?.data?.pagination || 
                     {
                       totalPages: 1,
                       currentPage: 1,
                       totalUsers: users.length,
                       hasPrev: false,
                       hasNext: false
                     };


  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
    setCurrentPage(1);
  };

  const handleStatusUpdate = (user) => {
    setSelectedUser(user);
    setShowModal(true);
  };

  const confirmStatusUpdate = () => {
    if (selectedUser) {
      updateStatusMutation.mutate({
        id: selectedUser.id,
        is_active: selectedUser.is_active
      });
    }
  };

  const handleCreateUser = () => {
    setEditingUser(null);
    setShowUserForm(true);
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
    setShowUserForm(true);
  };

  const handleSaveUser = (userData) => {
    if (editingUser) {
      updateUserMutation.mutate({ id: editingUser.id, data: userData });
    } else {
      createUserMutation.mutate(userData);
    }
  };

  const handleDeleteUser = (user) => {
    console.log('Delete user clicked for:', user.name, user.id);
    setUserToDelete(user);
    setShowDeleteModal(true);
  };

  const confirmDeleteUser = () => {
    if (userToDelete) {
      console.log('Confirming delete for user:', userToDelete.id);
      deleteUserMutation.mutate(userToDelete.id);
    } else {
      console.log('No user selected for deletion');
    }
  };

  const handleKYCStatusUpdate = async (user) => {
    setSelectedUserKYC(user);
    setShowKYCModal(true);
    setLoadingKYC(true);
    setKycData(null);

    try {
      // Fetch KYC data from the KYC verification table using user's displayCode
      const response = await adminAPI.getUserKYC(user.displayCode || user.id);
      setKycData(response.data);
    } catch (error) {
      console.error('Failed to fetch KYC data:', error);
      // If no KYC found, it's okay - user hasn't submitted KYC yet
      setKycData(null);
    } finally {
      setLoadingKYC(false);
    }
  };

  const confirmKYCUpdate = (newStatus, rejectionReason = null) => {
    if (kycData && kycData.id) {
      console.log('Updating KYC:', {
        kycId: kycData.id,
        kycStatus: newStatus,
        reviewer: 'Admin',
        rejectionReason: rejectionReason
      });
      
      updateKYCMutation.mutate({
        kycId: kycData.id,
        kycStatus: newStatus,
        reviewer: 'Admin', // You can get this from auth context
        rejectionReason: rejectionReason
      });
    } else {
      alert('No KYC verification found for this user.');
    }
  };

  // Helper function to get KYC status for a user
  const getUserKYCStatus = (user) => {
    if (!allKYCData?.data) return 'not_submitted';
    
    // Find KYC record for this user by userId or displayCode
    const kycRecord = allKYCData.data.find(kyc => 
      kyc.userId === user.id || 
      kyc.userId === user.displayCode ||
      kyc.user?.id === user.id ||
      kyc.user?.displayCode === user.displayCode
    );
    
    return kycRecord?.status || 'not_submitted';
  };

  const getKYCStatusBadge = (status) => {
    const statusMap = {
      'verified': { variant: 'success', text: 'Verified', icon: CheckCircle },
      'pending': { variant: 'warning', text: 'Pending', icon: Clock },
      'rejected': { variant: 'danger', text: 'Rejected', icon: XCircle },
      'not_submitted': { variant: 'default', text: 'Not Submitted', icon: XCircle }
    };
    return statusMap[status] || { variant: 'default', text: status, icon: Clock };
  };

  const getStatusBadge = (isActive) => {
    return isActive 
      ? { variant: 'success', text: 'Active', icon: UserCheck }
      : { variant: 'danger', text: 'Inactive', icon: UserX };
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  if (!isAuthenticated) {
    return (
      <div className="text-center py-12">
        <p className="text-gray-600">Please log in to view users.</p>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        <span className="ml-2 text-gray-600">Loading users...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <p className="text-red-600">Failed to load users</p>
        <Button onClick={() => window.location.reload()} className="mt-4">
          Retry
        </Button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Users Management</h2>
          <p className="text-gray-600">
            Manage all users in your platform
            {pagination.totalUsers > 0 && (
              <span className="ml-2 text-blue-600 font-medium">
                ({pagination.totalUsers} {pagination.totalUsers === 1 ? 'user' : 'users'})
              </span>
            )}
          </p>
        </div>
        <Button onClick={handleCreateUser} className="flex items-center space-x-2">
          <Plus className="w-4 h-4" />
          <span>Add User</span>
        </Button>
      </div>

      {/* Filters */}
      <Card className="p-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Search</label>
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search users..."
                value={filters.search}
                onChange={(e) => handleFilterChange('search', e.target.value)}
                className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Status</label>
            <select
              value={filters.status}
              onChange={(e) => handleFilterChange('status', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="">All Status</option>
              <option value="active">Active</option>
              <option value="inactive">Inactive</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">KYC Status</label>
            <select
              value={filters.kyc_status}
              onChange={(e) => handleFilterChange('kyc_status', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="">All KYC Status</option>
              <option value="verified">Verified</option>
              <option value="pending">Pending</option>
              <option value="rejected">Rejected</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Sort By</label>
            <select
              value={filters.sort_by}
              onChange={(e) => handleFilterChange('sort_by', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="created_at">Date Created</option>
              <option value="name">Name</option>
              <option value="email">Email</option>
              <option value="kyc_status">KYC Status</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Order</label>
            <select
              value={filters.sort_order}
              onChange={(e) => handleFilterChange('sort_order', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="desc">Descending</option>
              <option value="asc">Ascending</option>
            </select>
          </div>
        </div>
        
        {/* Additional Options */}
        <div className="mt-4 pt-4 border-t border-gray-200">
          <div className="flex items-center">
            <input
              type="checkbox"
              id="include_inactive"
              checked={filters.include_inactive}
              onChange={(e) => handleFilterChange('include_inactive', e.target.checked)}
              className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
            />
            <label htmlFor="include_inactive" className="ml-2 text-sm text-gray-700">
              Include inactive users (deleted users)
            </label>
          </div>
        </div>
      </Card>

      {/* Users Table */}
      <Card className="overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  User
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Contact
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  KYC Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Joined
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {users.map((user) => {
                // Get real KYC status from KYC table
                const userKYCStatus = getUserKYCStatus(user);
                const kycInfo = getKYCStatusBadge(userKYCStatus);
                const statusInfo = getStatusBadge(user.isActive !== undefined ? user.isActive : user.is_active);
                const KycIcon = kycInfo.icon;
                const StatusIcon = statusInfo.icon;

                return (
                  <tr key={user.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <div className="flex-shrink-0 h-10 w-10">
                          <div className="h-10 w-10 rounded-full bg-gray-200 flex items-center justify-center">
                            <span className="text-sm font-medium text-gray-700">
                              {(user.fullName || user.name || 'U').charAt(0).toUpperCase()}
                            </span>
                          </div>
                        </div>
                        <div className="ml-4">
                          <div className="flex items-center space-x-2">
                            <span className="text-sm font-medium text-gray-900">
                              {user.fullName || user.name || 'Unknown User'}
                            </span>
                            {user.displayCode && (
                              <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                                {user.displayCode}
                              </span>
                            )}
                            {!user.isActive && user.isActive !== undefined && (
                              <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-red-100 text-red-800">
                                Deleted
                              </span>
                            )}
                          </div>
                          <div className="text-sm text-gray-500">
                            ID: {user.id?.slice(0, 8)}...
                          </div>
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="space-y-1">
                        <div className="flex items-center text-sm text-gray-900">
                          <Mail className="w-3 h-3 mr-2 text-gray-400" />
                          {user.email}
                        </div>
                        {user.phone && (
                          <div className="flex items-center text-sm text-gray-500">
                            <Phone className="w-3 h-3 mr-2 text-gray-400" />
                            {user.phone}
                          </div>
                        )}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <Badge variant={statusInfo.variant} className="flex items-center">
                        <StatusIcon className="w-3 h-3 mr-1" />
                        {statusInfo.text}
                      </Badge>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <button 
                        onClick={() => handleKYCStatusUpdate(user)}
                        className="cursor-pointer hover:opacity-80 transition-opacity"
                        title="Click to update KYC status"
                      >
                        <Badge variant={kycInfo.variant} className="flex items-center">
                          <KycIcon className="w-3 h-3 mr-1" />
                          {kycInfo.text}
                        </Badge>
                      </button>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      <div className="flex items-center">
                        <Calendar className="w-3 h-3 mr-2 text-gray-400" />
                        {formatDate(user.createdAt || user.created_at)}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <div className="flex items-center space-x-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleEditUser(user)}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button 
                          variant="outline" 
                          size="sm"
                          onClick={() => handleStatusUpdate(user)}
                        >
                          <Shield className="w-4 h-4" />
                        </Button>
                        <Button 
                          variant="outline" 
                          size="sm" 
                          className={`${!(user.isActive !== undefined ? user.isActive : user.is_active) ? 'text-gray-400 cursor-not-allowed opacity-50' : 'text-red-600 hover:text-red-700 hover:bg-red-50'}`}
                          onClick={() => !(user.isActive !== undefined ? user.isActive : user.is_active) ? null : handleDeleteUser(user)}
                          title={!(user.isActive !== undefined ? user.isActive : user.is_active) ? 'User already deleted' : `Delete ${user.fullName || user.name}`}
                          disabled={!(user.isActive !== undefined ? user.isActive : user.is_active)}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>

        {/* Pagination */}
        {pagination.totalPages > 1 && (
          <div className="bg-white px-4 py-3 flex items-center justify-between border-t border-gray-200 sm:px-6">
            <div className="flex-1 flex justify-between sm:hidden">
              <Button
                variant="outline"
                onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                disabled={!pagination.hasPrev}
              >
                Previous
              </Button>
              <Button
                variant="outline"
                onClick={() => setCurrentPage(prev => Math.min(prev + 1, pagination.totalPages))}
                disabled={!pagination.hasNext}
              >
                Next
              </Button>
            </div>
            <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
              <div>
                <p className="text-sm text-gray-700">
                  Showing{' '}
                  <span className="font-medium">
                    {((currentPage - 1) * 10) + 1}
                  </span>{' '}
                  to{' '}
                  <span className="font-medium">
                    {Math.min(currentPage * 10, pagination.totalUsers)}
                  </span>{' '}
                  of{' '}
                  <span className="font-medium">{pagination.totalUsers}</span>{' '}
                  results
                </p>
              </div>
              <div>
                <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px">
                  {Array.from({ length: pagination.totalPages }, (_, i) => i + 1).map((page) => (
                    <button
                      key={page}
                      onClick={() => setCurrentPage(page)}
                      className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${
                        page === currentPage
                          ? 'z-10 bg-blue-50 border-blue-500 text-blue-600'
                          : 'bg-white border-gray-300 text-gray-500 hover:bg-gray-50'
                      }`}
                    >
                      {page}
                    </button>
                  ))}
                </nav>
              </div>
            </div>
          </div>
        )}
      </Card>

      {/* Status Update Modal */}
      {showModal && selectedUser && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">
                Update User Status
              </h3>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    User: {selectedUser.fullName || selectedUser.name}
                  </label>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Email: {selectedUser.email}
                  </label>
                </div>
                <div>
                  <label className="flex items-center">
                    <input
                      type="checkbox"
                      checked={selectedUser.isActive !== undefined ? selectedUser.isActive : selectedUser.is_active}
                      onChange={(e) => setSelectedUser(prev => ({ 
                        ...prev, 
                        isActive: e.target.checked,
                        is_active: e.target.checked 
                      }))}
                      className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <span className="ml-2 text-sm text-gray-700">Active User</span>
                  </label>
                </div>
              </div>
              <div className="flex justify-end space-x-3 mt-6">
                <Button
                  variant="outline"
                  onClick={() => setShowModal(false)}
                >
                  Cancel
                </Button>
                <Button
                  onClick={confirmStatusUpdate}
                  disabled={updateStatusMutation.isLoading}
                >
                  {updateStatusMutation.isLoading ? 'Updating...' : 'Update Status'}
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* User Form Modal */}
      {showUserForm && (
        <UserForm
          user={editingUser}
          onSave={handleSaveUser}
          onCancel={() => {
            setShowUserForm(false);
            setEditingUser(null);
          }}
          isLoading={createUserMutation.isLoading || updateUserMutation.isLoading}
        />
      )}

      {/* Delete Confirmation Modal */}
      {showDeleteModal && userToDelete && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <div className="flex items-center space-x-3 mb-4">
                <div className="p-2 bg-red-100 rounded-lg">
                  <AlertTriangle className="w-6 h-6 text-red-600" />
                </div>
                <div>
                  <h3 className="text-lg font-medium text-gray-900">Delete User</h3>
                  <p className="text-sm text-gray-600">This action cannot be undone</p>
                </div>
              </div>
              <p className="text-sm text-gray-700 mb-6">
                Are you sure you want to delete <strong>{userToDelete.fullName || userToDelete.name}</strong>? 
                This will deactivate the user account.
              </p>
              <div className="flex justify-end space-x-3">
                <Button
                  variant="outline"
                  onClick={() => {
                    setShowDeleteModal(false);
                    setUserToDelete(null);
                  }}
                  disabled={deleteUserMutation.isLoading}
                >
                  Cancel
                </Button>
                <Button
                  onClick={confirmDeleteUser}
                  disabled={deleteUserMutation.isLoading}
                  className="bg-red-600 hover:bg-red-700"
                >
                  {deleteUserMutation.isLoading ? 'Deleting...' : 'Delete User'}
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* KYC Status Update Modal */}
      {showKYCModal && selectedUserKYC && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-full max-w-2xl shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <div className="flex items-center space-x-3 mb-6">
                <div className="p-2 bg-blue-100 rounded-lg">
                  <Shield className="w-6 h-6 text-blue-600" />
                </div>
                <div>
                  <h3 className="text-lg font-medium text-gray-900">Update KYC Status</h3>
                  <p className="text-sm text-gray-600">Review and approve user verification</p>
                </div>
              </div>

              {/* User Information */}
              <div className="bg-gray-50 p-4 rounded-lg mb-6">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <p className="text-xs text-gray-500 mb-1">User Name</p>
                    <p className="text-sm font-medium text-gray-900">{selectedUserKYC.fullName || selectedUserKYC.name}</p>
                  </div>
                  <div>
                    <p className="text-xs text-gray-500 mb-1">User ID</p>
                    <p className="text-sm font-medium text-gray-900">{selectedUserKYC.displayCode || selectedUserKYC.id?.slice(0, 8)}</p>
                  </div>
                  <div>
                    <p className="text-xs text-gray-500 mb-1">Email</p>
                    <p className="text-sm font-medium text-gray-900">{selectedUserKYC.email}</p>
                  </div>
                  <div>
                    <p className="text-xs text-gray-500 mb-1">Phone</p>
                    <p className="text-sm font-medium text-gray-900">{selectedUserKYC.phone || 'N/A'}</p>
                  </div>
                  <div>
                    <p className="text-xs text-gray-500 mb-1">Current KYC Status</p>
                    {loadingKYC ? (
                      <span className="text-sm text-gray-500">Loading...</span>
                    ) : (
                      <Badge variant={getKYCStatusBadge(kycData?.status || 'not_submitted').variant} className="inline-flex">
                        {getKYCStatusBadge(kycData?.status || 'not_submitted').text}
                      </Badge>
                    )}
                  </div>
                  <div>
                    <p className="text-xs text-gray-500 mb-1">Joined Date</p>
                    <p className="text-sm font-medium text-gray-900">{formatDate(selectedUserKYC.createdAt || selectedUserKYC.created_at)}</p>
                  </div>
                </div>
              </div>

              {/* KYC Details from KYC Verification Table */}
              <div className="mb-6">
                <h4 className="text-sm font-medium text-gray-900 mb-3">KYC Verification Details</h4>
                <div className="bg-white border border-gray-200 rounded-lg p-4 space-y-3">
                  {loadingKYC ? (
                    <div className="text-center py-4">
                      <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-600 mx-auto"></div>
                      <p className="text-sm text-gray-500 mt-2">Loading KYC data...</p>
                    </div>
                  ) : kycData ? (
                    <>
                      {/* Verified Success Banner */}
                      {kycData.status === 'verified' && (
                        <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-4">
                          <div className="flex items-center">
                            <CheckCircle className="w-6 h-6 text-green-600 mr-3" />
                            <div>
                              <p className="text-sm font-semibold text-green-800">KYC Verified Successfully</p>
                              <p className="text-xs text-green-600 mt-1">
                                This user has been verified and can access all features. Status cannot be reverted.
                              </p>
                            </div>
                          </div>
                        </div>
                      )}

                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <p className="text-xs text-gray-500 mb-1">Verification Type</p>
                          <p className="text-sm text-gray-900 uppercase">{kycData.type || 'Not provided'}</p>
                        </div>
                        <div>
                          <p className="text-xs text-gray-500 mb-1">Submission Date</p>
                          <p className="text-sm text-gray-900">{kycData.submittedAt ? formatDate(kycData.submittedAt) : 'Not provided'}</p>
                        </div>
                        <div>
                          <p className="text-xs text-gray-500 mb-1">Reviewed By</p>
                          <p className="text-sm text-gray-900">{kycData.reviewer || 'Not reviewed yet'}</p>
                        </div>
                        <div>
                          <p className="text-xs text-gray-500 mb-1">Reviewed Date</p>
                          <p className="text-sm text-gray-900">{kycData.reviewedAt ? formatDate(kycData.reviewedAt) : 'Not reviewed yet'}</p>
                        </div>
                      </div>
                      
                      {/* Document Links */}
                      <div className="pt-3 border-t space-y-2">
                        <p className="text-xs text-gray-500 mb-2">Uploaded Documents:</p>
                        {kycData.documentFrontUrl && (
                          <div>
                            <a 
                              href={kycData.documentFrontUrl} 
                              target="_blank" 
                              rel="noopener noreferrer"
                              className="text-blue-600 hover:text-blue-800 text-sm underline block"
                            >
                              📄 View Front Document
                            </a>
                          </div>
                        )}
                        {kycData.documentBackUrl && (
                          <div>
                            <a 
                              href={kycData.documentBackUrl} 
                              target="_blank" 
                              rel="noopener noreferrer"
                              className="text-blue-600 hover:text-blue-800 text-sm underline block"
                            >
                              📄 View Back Document
                            </a>
                          </div>
                        )}
                        {kycData.selfieUrl && (
                          <div>
                            <a 
                              href={kycData.selfieUrl} 
                              target="_blank" 
                              rel="noopener noreferrer"
                              className="text-blue-600 hover:text-blue-800 text-sm underline block"
                            >
                              🤳 View Selfie
                            </a>
                          </div>
                        )}
                        {!kycData.documentFrontUrl && !kycData.documentBackUrl && !kycData.selfieUrl && (
                          <p className="text-sm text-gray-500 italic">No documents uploaded</p>
                        )}
                      </div>

                      {/* Additional Metadata */}
                      {kycData.metadata && Object.keys(kycData.metadata).length > 0 && (
                        <div className="pt-3 border-t">
                          <p className="text-xs text-gray-500 mb-2">Additional Information:</p>
                          <pre className="text-xs bg-gray-50 p-2 rounded overflow-auto">
                            {JSON.stringify(kycData.metadata, null, 2)}
                          </pre>
                        </div>
                      )}

                      {/* Rejection Reason */}
                      {kycData.status === 'rejected' && kycData.rejectionReason && (
                        <div className="pt-3 border-t bg-red-50 p-3 rounded">
                          <p className="text-xs text-red-600 font-medium mb-1">Rejection Reason:</p>
                          <p className="text-sm text-red-900">{kycData.rejectionReason}</p>
                        </div>
                      )}
                    </>
                  ) : (
                    <div className="text-center py-8 bg-gray-50 rounded-lg">
                      <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-gray-200 mb-3">
                        <XCircle className="w-8 h-8 text-gray-400" />
                      </div>
                      <p className="text-base font-semibold text-gray-700 mb-1">KYC Not Submitted</p>
                      <p className="text-sm text-gray-500">User hasn't submitted their KYC documents yet</p>
                      <div className="mt-4 p-3 bg-blue-50 rounded-md mx-8">
                        <p className="text-xs text-blue-700">
                          <strong>Note:</strong> User must submit their CNIC/documents before you can verify their account.
                        </p>
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex items-center justify-between pt-4 border-t">
                <Button
                  variant="outline"
                  onClick={() => {
                    setShowKYCModal(false);
                    setSelectedUserKYC(null);
                    setKycData(null);
                  }}
                  disabled={updateKYCMutation.isLoading}
                >
                  Close
                </Button>
                {kycData ? (
                  kycData.status === 'verified' ? (
                    // If KYC is verified, show message that it cannot be changed
                    <div className="flex items-center space-x-2">
                      <CheckCircle className="w-5 h-5 text-green-600" />
                      <p className="text-sm font-medium text-green-600">
                        KYC is verified and cannot be reverted
                      </p>
                    </div>
                  ) : (
                    // If KYC is pending or rejected, show action buttons
                    <div className="flex space-x-3">
                      <Button
                        onClick={() => {
                          const reason = prompt('Enter rejection reason (optional):');
                          if (reason !== null) { // null means user clicked Cancel
                            confirmKYCUpdate('rejected', reason || 'No reason provided');
                          }
                        }}
                        disabled={updateKYCMutation.isLoading || kycData.status === 'rejected'}
                        className="bg-red-600 hover:bg-red-700 text-white disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        <XCircle className="w-4 h-4 mr-2" />
                        {kycData.status === 'rejected' ? 'Already Rejected' : 'Reject KYC'}
                      </Button>
                      <Button
                        onClick={() => confirmKYCUpdate('pending')}
                        disabled={updateKYCMutation.isLoading || kycData.status === 'pending'}
                        className="bg-yellow-600 hover:bg-yellow-700 text-white disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        <Clock className="w-4 h-4 mr-2" />
                        {kycData.status === 'pending' ? 'Already Pending' : 'Mark Pending'}
                      </Button>
                      <Button
                        onClick={() => confirmKYCUpdate('verified')}
                        disabled={updateKYCMutation.isLoading}
                        className="bg-green-600 hover:bg-green-700 text-white disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        <CheckCircle className="w-4 h-4 mr-2" />
                        {updateKYCMutation.isLoading ? 'Approving...' : 'Approve KYC'}
                      </Button>
                    </div>
                  )
                ) : (
                  <p className="text-sm text-gray-500 italic">No KYC submitted - User needs to submit KYC documents first</p>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UsersManagement;
