import React, { useState } from 'react';
import { useMutation } from 'react-query';
import { X, Building2, Globe, Image, FileText, Mail, User, Key } from 'lucide-react';
import Button from '../ui/Button';
import Input from '../ui/Input';
import { orgAdminAPI } from '../../services/api';

const CreateOrganizationModal = ({ onClose, onSuccess }) => {
  const [autoGenerate, setAutoGenerate] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    website: '',
    logoUrl: '',
    adminEmail: '',
    adminPassword: '',
    adminFullName: ''
  });

  const createMutation = useMutation(
    (data) => orgAdminAPI.createOrganization(data),
    {
      onSuccess: (response) => {
        console.log('✅ Organization created:', response);
        const credentials = {
          email: response.data?.admin?.email || formData.adminEmail,
          password: response.data?.temporaryPassword || formData.adminPassword,
          fullName: response.data?.admin?.fullName || formData.adminFullName,
          organizationName: response.data?.organization?.name || formData.name,
          organizationId: response.data?.organization?.displayCode || response.data?.organization?.id
        };
        onSuccess(credentials);
        onClose();
      },
      onError: (error) => {
        console.error('❌ Create organization error:', error);

        // Check if it's a "Cannot POST" or 404 error (API not implemented)
        const errorMessage = error.response?.data?.message || error.message || '';

        if (errorMessage.includes('Cannot POST') || error.response?.status === 404) {
          alert(
            '⚠️ Backend API Not Ready\n\n' +
            'The organization creation API endpoint is not yet implemented on the backend.\n\n' +
            'Required endpoint: POST /admin/organizations\n\n' +
            'Please ask the backend team to implement this endpoint first.\n\n' +
            'See ORGANIZATION_MANAGEMENT_COMPLETE.md for API requirements.'
          );
        } else {
          alert(`Error creating organization: ${errorMessage}`);
        }
      }
    }
  );

  const handleSubmit = (e) => {
    e.preventDefault();

    const payload = {
      name: formData.name.trim(),
      description: formData.description.trim(),
      website: formData.website.trim(),
      logoUrl: formData.logoUrl.trim(),
      autoGenerateCredentials: autoGenerate
    };

    // If not auto-generating, include manual credentials
    if (!autoGenerate) {
      payload.adminEmail = formData.adminEmail.trim();
      payload.adminPassword = formData.adminPassword;
      payload.adminFullName = formData.adminFullName.trim();
    }

    console.log('🚀 Creating organization with payload:', payload);
    createMutation.mutate(payload);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="sticky top-0 bg-white border-b px-6 py-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="p-2 bg-blue-100 rounded-lg">
              <Building2 className="w-5 h-5 text-blue-600" />
            </div>
            <h2 className="text-xl font-bold text-gray-900">Create New Organization</h2>
          </div>
          <button onClick={onClose} className="p-2 hover:bg-gray-100 rounded-lg transition">
            <X className="w-5 h-5 text-gray-500" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          {/* Backend API Warning */}
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <div className="flex gap-3">
              <span className="text-xl">⚠️</span>
              <div className="flex-1">
                <p className="font-medium text-yellow-900 text-sm">Backend API Required</p>
                <p className="text-xs text-yellow-800 mt-1">
                  The <code className="bg-yellow-100 px-1 rounded">POST /admin/organizations</code> endpoint 
                  must be implemented on the backend before you can create organizations.
                  See <strong>ORGANIZATION_MANAGEMENT_COMPLETE.md</strong> for API details.
                </p>
              </div>
            </div>
          </div>

          {/* Organization Details */}
          <div className="space-y-4">
            <h3 className="text-sm font-semibold text-gray-900 uppercase">Organization Details</h3>

            <div className="relative">
              <Building2 className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
              <Input
                label="Organization Name"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                placeholder="e.g., HMR Builders"
                className="pl-10"
                required
              />
            </div>

            <div className="relative">
              <FileText className="absolute left-3 top-9 w-5 h-5 text-gray-400" />
              <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                className="w-full pl-10 px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                rows={3}
                placeholder="Brief description of the organization..."
              />
            </div>

            <div className="relative">
              <Globe className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
              <Input
                label="Website"
                value={formData.website}
                onChange={(e) => setFormData({ ...formData, website: e.target.value })}
                placeholder="https://example.com"
                type="url"
                className="pl-10"
              />
            </div>

            <div className="relative">
              <Image className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
              <Input
                label="Logo URL"
                value={formData.logoUrl}
                onChange={(e) => setFormData({ ...formData, logoUrl: e.target.value })}
                placeholder="https://example.com/logo.png"
                type="url"
                className="pl-10"
              />
            </div>
          </div>

          {/* Admin Credentials Section */}
          <div className="border-t pt-6 space-y-4">
            <div className="flex items-center justify-between">
              <h3 className="text-sm font-semibold text-gray-900 uppercase">Admin Credentials</h3>
              <label className="flex items-center gap-2 cursor-pointer">
                <input
                  type="checkbox"
                  checked={autoGenerate}
                  onChange={(e) => setAutoGenerate(e.target.checked)}
                  className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                />
                <span className="text-sm text-gray-700">Auto-generate credentials</span>
              </label>
            </div>

            {autoGenerate ? (
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                <p className="text-sm text-blue-800">
                  ✨ A random email and secure password will be automatically generated for the organization admin.
                  You'll see the credentials after creation.
                </p>
              </div>
            ) : (
              <div className="space-y-4">
                <div className="relative">
                  <Mail className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
                  <Input
                    label="Admin Email"
                    value={formData.adminEmail}
                    onChange={(e) => setFormData({ ...formData, adminEmail: e.target.value })}
                    placeholder="admin@organization.com"
                    type="email"
                    className="pl-10"
                    required={!autoGenerate}
                  />
                </div>

                <div className="relative">
                  <User className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
                  <Input
                    label="Admin Full Name"
                    value={formData.adminFullName}
                    onChange={(e) => setFormData({ ...formData, adminFullName: e.target.value })}
                    placeholder="John Doe"
                    className="pl-10"
                    required={!autoGenerate}
                  />
                </div>

                <div className="relative">
                  <Key className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
                  <Input
                    label="Admin Password"
                    value={formData.adminPassword}
                    onChange={(e) => setFormData({ ...formData, adminPassword: e.target.value })}
                    placeholder="Secure password (min 8 characters)"
                    type="password"
                    className="pl-10"
                    required={!autoGenerate}
                    minLength={8}
                  />
                </div>
              </div>
            )}
          </div>

          {/* Actions */}
          <div className="flex gap-3 justify-end pt-4 border-t">
            <Button type="button" variant="outline" onClick={onClose}>
              Cancel
            </Button>
            <Button type="submit" disabled={createMutation.isLoading}>
              {createMutation.isLoading ? 'Creating...' : 'Create Organization'}
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateOrganizationModal;
