                                    walletBalance,
                                    totalInvestments,
                                    totalReturns,
                                    totalCurrentValue,
                                    totalNetROI,
                                    activeInvestments,
                                    averageROI,
                                    propertiesInvested,
                                    investments: investments.length,
                                    transactions: transactions.length
                                  });

