                                    walletBalance,
                                    totalInvestments,
                                    totalReturns,
                                    totalCurrentValue,
                                    totalNetROI,
                                    activeInvestments,
                                    averageROI,
                                    propertiesInvested,
                                    investments: investments,
                                    transactions: transactions
                                  });
