                                    walletBalance: 0,
                                    totalInvestments: 0,
                                    totalReturns: 0,
                                    propertiesInvested: 0,
                                    investments: [],
                                    transactions: []
                                  });
                                } finally {
                                  setLoadingFinancialData(false);
                                }
