import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Shield, Eye, EyeOff, LogIn, Globe, Building2 } from 'lucide-react';
import Card from '../../components/ui/Card';
import Button from '../../components/ui/Button';
import Badge from '../../components/ui/Badge';
import { useAdminAuth } from '../../components/admin/AdminAuth';
import { useOrganization } from '../../contexts/OrganizationContext';

const AdminLogin = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { login, isAuthenticated } = useAdminAuth();
  const { organization, subdomain, isAdmin } = useOrganization();

  // Redirect if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/admin');
    }
  }, [isAuthenticated, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      // For demo purposes, we'll accept any credentials
      // In production, you would validate against admin users in database
      if (formData.email && formData.password) {
        console.log('Attempting login with:', formData.email);
        
        // Simulate API call
        await new Promise(resolve => setTimeout(resolve, 1000));
        
        // Use the context login function
        const userData = {
          id: 'admin-demo',
          name: 'Admin User',
          email: formData.email,
          role: 'admin'
        };
        
        console.log('Calling login with userData:', userData);
        try {
          login(userData);
          console.log('Login called, navigating to /admin');
          navigate('/admin');
        } catch (error) {
          console.error('Context login failed, using fallback:', error);
          fallbackLogin(userData);
        }
      } else {
        setError('Please enter both email and password');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('Login failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDemoLogin = () => {
    setFormData({
      email: 'admin@hmrbuilders.com',
      password: 'admin123'
    });
  };

  // Fallback login function in case context doesn't work
  const fallbackLogin = (userData) => {
    console.log('Using fallback login');
    localStorage.setItem('adminSession', 'true');
    localStorage.setItem('adminUser', JSON.stringify(userData));
    navigate('/admin');
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        {/* Organization Banner */}
        {!isAdmin && organization && (
          <div className="bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-lg p-4 shadow-lg">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <Building2 className="w-6 h-6" />
                <div>
                  <p className="text-sm font-medium opacity-90">Logging into</p>
                  <p className="text-lg font-bold">{organization} Organization</p>
                </div>
              </div>
              <Badge className="bg-white text-blue-600 font-semibold">
                {subdomain}
              </Badge>
            </div>
          </div>
        )}

        {isAdmin && (
          <div className="bg-gradient-to-r from-purple-500 to-purple-600 text-white rounded-lg p-4 shadow-lg">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <Globe className="w-6 h-6" />
                <div>
                  <p className="text-sm font-medium opacity-90">Super Admin Mode</p>
                  <p className="text-lg font-bold">All Organizations</p>
                </div>
              </div>
              <Badge className="bg-white text-purple-600 font-semibold">
                ADMIN
              </Badge>
            </div>
          </div>
        )}

        <div className="text-center">
          <div className={`mx-auto h-12 w-12 ${isAdmin ? 'bg-purple-100' : 'bg-blue-100'} rounded-full flex items-center justify-center`}>
            <Shield className={`h-6 w-6 ${isAdmin ? 'text-purple-600' : 'text-blue-600'}`} />
          </div>
          <h2 className="mt-6 text-3xl font-extrabold text-gray-900">
            Admin Login
          </h2>
          <p className="mt-2 text-sm text-gray-600">
            {isAdmin ? 'Sign in to manage all organizations' : `Sign in to manage ${organization || 'the'} organization`}
          </p>
        </div>

        <Card className="p-8">
          <form onSubmit={handleSubmit} className="space-y-6">
            {error && (
              <div className="bg-red-50 border border-red-200 rounded-md p-3">
                <p className="text-sm text-red-600">{error}</p>
              </div>
            )}

            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                Email Address
              </label>
              <input
                id="email"
                name="email"
                type="email"
                required
                value={formData.email}
                onChange={handleChange}
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                placeholder="Enter your email"
              />
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                Password
              </label>
              <div className="mt-1 relative">
                <input
                  id="password"
                  name="password"
                  type={showPassword ? 'text' : 'password'}
                  required
                  value={formData.password}
                  onChange={handleChange}
                  className="block w-full px-3 py-2 pr-10 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                  placeholder="Enter your password"
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-3 flex items-center"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? (
                    <EyeOff className="h-4 w-4 text-gray-400" />
                  ) : (
                    <Eye className="h-4 w-4 text-gray-400" />
                  )}
                </button>
              </div>
            </div>

            <div>
              <Button
                type="submit"
                disabled={isLoading}
                className="w-full flex items-center justify-center space-x-2"
              >
                <LogIn className="w-4 h-4" />
                <span>{isLoading ? 'Signing in...' : 'Sign in'}</span>
              </Button>
            </div>

            <div className="text-center">
              <button
                type="button"
                onClick={handleDemoLogin}
                className="text-sm text-blue-600 hover:text-blue-500"
              >
                Use Demo Credentials
              </button>
            </div>
          </form>

          <div className={`mt-6 p-4 ${isAdmin ? 'bg-purple-50' : 'bg-blue-50'} rounded-md`}>
            <h3 className={`text-sm font-medium ${isAdmin ? 'text-purple-800' : 'text-blue-800'} mb-2`}>
              Demo Credentials:
            </h3>
            <p className={`text-xs ${isAdmin ? 'text-purple-700' : 'text-blue-700'}`}>
              <strong>Email:</strong> admin@hmrbuilders.com<br />
              <strong>Password:</strong> admin123
            </p>
            {!isAdmin && organization && (
              <p className="text-xs text-blue-600 mt-2">
                ‚ÑπÔ∏è You will only see {organization} organization data after login
              </p>
            )}
            {isAdmin && (
              <p className="text-xs text-purple-600 mt-2">
                ‚ÑπÔ∏è Admin mode - You will see data from all organizations
              </p>
            )}
          </div>
        </Card>

        {/* Debug Info (Remove in production) */}
        <div className="text-center text-xs text-gray-500 space-y-1">
          <p>üîç Debug Info:</p>
          <p>Hostname: {window.location.hostname}</p>
          <p>Subdomain: {subdomain || 'none'}</p>
          <p>Organization: {organization || 'Admin Mode'}</p>
          <p>Is Admin: {isAdmin ? 'Yes' : 'No'}</p>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
