import React, { useState, useEffect } from 'react';
import { useQuery } from 'react-query';
import { useNavigate } from 'react-router-dom';
import { 
  Users, 
  Building2, 
  TrendingUp, 
  DollarSign, 
  Activity,
  BarChart3,
  Shield,
  LogOut,
  ArrowUpRight,
  Calendar
} from 'lucide-react';
import Card from '../../components/ui/Card';
import Button from '../../components/ui/Button';
import Badge from '../../components/ui/Badge';
import { organizationsAPI, adminAPI } from '../../services/api';
import { useOrganizationAuth } from '../../components/organization/OrganizationAuth';
import OrgPropertiesManagement from './OrgPropertiesManagement';
import OrgUsersManagement from './OrgUsersManagement';
import OrgTransactionsManagement from './OrgTransactionsManagement';
import OrgInvestmentsManagement from './OrgInvestmentsManagement';

const OrgDashboard = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const { organizationUser, isAuthenticated, logout } = useOrganizationAuth();
  const navigate = useNavigate();

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/org/login');
    }
  }, [isAuthenticated, navigate]);

  // Get organization info
  const organizationName = organizationUser?.organizationName || 'Organization';
  const organizationSlug = organizationUser?.organizationSlug || '';
  const organizationId = organizationUser?.organizationId || '';

  // Update browser tab title with organization name
  useEffect(() => {
    if (organizationName) {
      document.title = `${organizationName} - Organization Dashboard`;
    }
    return () => {
      document.title = 'HMR Platform'; // Reset on unmount
    };
  }, [organizationName]);

  // Debug: Log organization info
  useEffect(() => {
    console.log('ðŸ¢ Current Organization Info:', {
      name: organizationName,
      slug: organizationSlug,
      id: organizationId,
      email: organizationUser?.email,
      fullData: organizationUser
    });
  }, [organizationName, organizationSlug, organizationId, organizationUser]);

  // Fetch organization details
  const { data: orgData, isLoading: orgLoading } = useQuery(
    ['org-details', organizationId],
    async () => {
      console.log('ðŸ”„ Fetching organization details for ID:', organizationId);
      const response = await organizationsAPI.getById(organizationId);
      console.log('âœ… Organization details:', response);
      return response;
    },
    { 
      refetchInterval: 30000,
      enabled: isAuthenticated && !!organizationId,
      retry: 1,
      onError: (error) => {
        console.log('Organization API error:', error.message);
      }
    }
  );

  // Fetch comprehensive dashboard data (NEW!)
  const { data: dashboardData, isLoading: dashboardDataLoading } = useQuery(
    ['org-dashboard-data', organizationId],
    async () => {
      try {
        console.log('ðŸ”„ Fetching dashboard data for organization:', organizationId);
        const response = await organizationsAPI.getDashboard(organizationId);
        console.log('âœ… Dashboard Data:', response);
        return response;
      } catch (error) {
        console.log('â„¹ï¸ Dashboard endpoint not available, using individual endpoints');
        return null;
      }
    },
    { 
      enabled: isAuthenticated && !!organizationId,
      retry: 1
    }
  );

  // Fetch organization transactions to count
  const { data: transactionsData, error: transactionsError } = useQuery(
    ['org-transactions-count', organizationId],
    async () => {
      console.log('ðŸ”„ Fetching transactions for organization ID:', organizationId);
      const response = await organizationsAPI.getTransactions(organizationId);
      console.log('âœ… Transactions Response:', {
        status: response.status,
        data: response.data,
        fullResponse: response
      });
      return response;
    },
    { 
      enabled: isAuthenticated && !!organizationId,
      retry: 1,
      onError: (error) => {
        console.error('âŒ Transactions fetch error:', error);
        console.error('Error details:', error.response?.data || error.message);
      }
    }
  );

  // Fetch organization liquidity/analytics
  const { data: liquidityData } = useQuery(
    ['org-liquidity', organizationId],
    async () => {
      try {
        console.log('ðŸ”„ Fetching liquidity analytics for organization:', organizationId);
        const response = await organizationsAPI.getLiquidity(organizationId);
        console.log('âœ… Liquidity data:', response);
        return response;
      } catch (error) {
        console.log('â„¹ï¸ Liquidity endpoint error:', error.message);
        return null;
      }
    },
    { 
      enabled: isAuthenticated && !!organizationId,
      retry: 1
    }
  );

  // Fetch properties to count (ORGANIZATION-SPECIFIC ONLY!)
  const { data: propertiesCountData } = useQuery(
    ['org-properties-count', organizationId],
    async () => {
      try {
        console.log(`ðŸ¢ Fetching ONLY ${organizationName} properties (organizationId: ${organizationId})`);
        const response = await organizationsAPI.getProperties(organizationId);
        const properties = response.data?.properties || response.data?.data?.properties || response.data || [];
        console.log(`âœ… Fetched ${Array.isArray(properties) ? properties.length : 0} properties for ${organizationName} (${organizationId})`);
        return response;
      } catch (error) {
        console.log(`âš ï¸ Organization API failed, falling back to admin API with organizationId filter: ${organizationId}`);
        // Fallback to admin API with organizationId filter
        const response = await adminAPI.getProperties({ organizationId, limit: 1000 });
        const properties = response.data?.properties || response.data?.data?.properties || response.data || [];
        console.log(`âœ… Fallback: Fetched ${Array.isArray(properties) ? properties.length : 0} properties for ${organizationName}`);
        return response;
      }
    },
    { 
      enabled: isAuthenticated && !!organizationId,
      retry: 1
    }
  );

  // Fetch users to count
  const { data: usersCountData } = useQuery(
    ['org-users-count', organizationId],
    async () => {
      try {
        console.log('ðŸ”„ Fetching users count for organization:', organizationId);
        const response = await organizationsAPI.getUsers(organizationId);
        console.log('âœ… Users count data:', response);
        return response;
      } catch (error) {
        // Fallback to admin API
        const response = await adminAPI.getUsers({ organizationId, limit: 1000 });
        return response;
      }
    },
    { 
      enabled: isAuthenticated && !!organizationId,
      retry: 1
    }
  );

  // Fetch investments to count
  const { data: investmentsCountData } = useQuery(
    ['org-investments-count', organizationId],
    async () => {
      try {
        console.log('ðŸ”„ Fetching investments count for organization:', organizationId);
        const response = await organizationsAPI.getInvestments(organizationId);
        console.log('âœ… Investments count data:', response);
        return response;
      } catch (error) {
        // Fallback to admin API
        const response = await adminAPI.getInvestments({ organizationId, limit: 1000 });
        return response;
      }
    },
    { 
      enabled: isAuthenticated && !!organizationId,
      retry: 1
    }
  );

  // Calculate statistics from fetched data
  const transactions = transactionsData?.data?.transactions || 
                      transactionsData?.data?.data?.transactions || 
                      transactionsData?.data || 
                      [];
  const properties = propertiesCountData?.data?.properties || 
                    propertiesCountData?.data?.data?.properties || 
                    propertiesCountData?.data || 
                    [];
  const users = usersCountData?.data?.users || 
               usersCountData?.data?.data?.users || 
               usersCountData?.data || 
               [];
  const investments = investmentsCountData?.data?.investments || 
                     investmentsCountData?.data?.data?.investments || 
                     investmentsCountData?.data || 
                     [];
  
  // Parse dashboard data if available
  const dashboardStats = dashboardData?.data?.data || dashboardData?.data || null;
  
  console.log('ðŸ“Š Dashboard API Response:', {
    hasDashboardData: !!dashboardStats,
    dashboardStats: dashboardStats
  });
  
  // Debug logging
  console.log('ðŸ“Š Individual Endpoint Data Summary:', {
    organizationId,
    organizationName,
    transactions: {
      count: Array.isArray(transactions) ? transactions.length : 0,
      data: transactions
    },
    properties: {
      count: Array.isArray(properties) ? properties.length : 0,
      data: properties
    },
    users: {
      count: Array.isArray(users) ? users.length : 0,
      data: users
    },
    investments: {
      count: Array.isArray(investments) ? investments.length : 0,
      data: investments
    }
  });
  
  // Use dashboard data if available, otherwise calculate from individual endpoints
  const stats = dashboardStats ? {
    // Use dashboard endpoint data
    totalUsers: dashboardStats.totalUsers || dashboardStats.usersCount || (Array.isArray(users) ? users.length : 0),
    totalProperties: dashboardStats.totalProperties || dashboardStats.propertiesCount || (Array.isArray(properties) ? properties.length : 0),
    totalTransactions: dashboardStats.totalTransactions || dashboardStats.transactionsCount || (Array.isArray(transactions) ? transactions.length : 0),
    totalInvestments: dashboardStats.totalInvestments || dashboardStats.investmentsAmount || 
      (Array.isArray(investments) ? investments.reduce((sum, inv) => sum + parseFloat(inv.amount || inv.invested_amount || 0), 0) : 0),
    totalInvestmentCount: dashboardStats.investmentsCount || (Array.isArray(investments) ? investments.length : 0),
    totalTransactionAmount: dashboardStats.transactionsAmount || 
      (Array.isArray(transactions) ? transactions.reduce((sum, tx) => sum + parseFloat(tx.amount || 0), 0) : 0),
    activeInvestments: dashboardStats.activeInvestments || 
      (Array.isArray(investments) ? investments.filter(inv => inv.status === 'active' || inv.status === 'approved').length : 0),
    pendingInvestments: dashboardStats.pendingInvestments || 
      (Array.isArray(investments) ? investments.filter(inv => inv.status === 'pending').length : 0),
    totalPropertyValue: dashboardStats.totalPropertyValue || 
      (Array.isArray(properties) ? properties.reduce((sum, prop) => sum + parseFloat(prop.price || prop.totalValue || 0), 0) : 0),
    liquidityData: liquidityData?.data?.data || liquidityData?.data || null
  } : {
    // Calculate from individual endpoints
    totalUsers: Array.isArray(users) ? users.length : 0,
    totalProperties: Array.isArray(properties) ? properties.length : 0,
    totalTransactions: Array.isArray(transactions) ? transactions.length : 0,
    totalInvestments: Array.isArray(investments) 
      ? investments.reduce((sum, inv) => sum + parseFloat(inv.amount || inv.invested_amount || 0), 0)
      : 0,
    totalInvestmentCount: Array.isArray(investments) ? investments.length : 0,
    totalTransactionAmount: Array.isArray(transactions) 
      ? transactions.reduce((sum, tx) => sum + parseFloat(tx.amount || 0), 0) 
      : 0,
    activeInvestments: Array.isArray(investments) 
      ? investments.filter(inv => inv.status === 'active' || inv.status === 'approved').length 
      : 0,
    pendingInvestments: Array.isArray(investments) 
      ? investments.filter(inv => inv.status === 'pending').length 
      : 0,
    totalPropertyValue: Array.isArray(properties) 
      ? properties.reduce((sum, prop) => sum + parseFloat(prop.price || prop.totalValue || 0), 0) 
      : 0,
    liquidityData: liquidityData?.data?.data || liquidityData?.data || null
  };
  
  console.log('ðŸ“ˆ Final Stats (using ' + (dashboardStats ? 'DASHBOARD' : 'INDIVIDUAL') + ' endpoints):', stats);

  const dashboardLoading = orgLoading;

  const tabs = [
    { id: 'overview', label: 'Overview', icon: BarChart3 },
    { id: 'properties', label: 'Properties', icon: Building2 },
    { id: 'users', label: 'Users', icon: Users },
    { id: 'transactions', label: 'Transactions', icon: DollarSign },
    { id: 'investments', label: 'Investments', icon: TrendingUp }
  ];

  // Format currency
  const formatCurrency = (amount, currency = 'USDT') => {
    if (!amount) return `0 ${currency}`;
    return `${parseFloat(amount).toLocaleString()} ${currency}`;
  };

  // Stat Card Component
  const StatCard = ({ title, value, icon: Icon, color = 'blue', onClick }) => (
    <Card 
      className={`p-6 ${onClick ? 'cursor-pointer hover:shadow-lg transition-all duration-200 hover:scale-105' : ''}`}
      onClick={onClick}
    >
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <p className="text-sm font-medium text-gray-600">{title}</p>
          <p className="text-3xl font-bold text-gray-900 mt-2">{value}</p>
        </div>
        <div className={`p-4 rounded-2xl bg-gradient-to-br from-${color}-100 to-${color}-200`}>
          <Icon className={`w-8 h-8 text-${color}-600`} />
        </div>
      </div>
    </Card>
  );

  const handleLogout = () => {
    logout();
    navigate('/org/login');
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'properties':
        return <OrgPropertiesManagement organizationId={organizationId} />;
      case 'users':
        return <OrgUsersManagement organizationId={organizationId} />;
      case 'transactions':
        return <OrgTransactionsManagement organizationId={organizationId} />;
      case 'investments':
        return <OrgInvestmentsManagement organizationId={organizationId} />;
      default:
        return renderOverview();
    }
  };

  const renderOverview = () => (
    <div className="space-y-6">
      {/* Primary Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Users"
          value={stats.totalUsers || 0}
          icon={Users}
          color="blue"
          onClick={() => setActiveTab('users')}
        />
        <StatCard
          title="Total Properties"
          value={stats.totalProperties || 0}
          icon={Building2}
          color="green"
          onClick={() => setActiveTab('properties')}
        />
        <StatCard
          title="Total Investments"
          value={formatCurrency(stats.totalInvestments || 0)}
          icon={TrendingUp}
          color="purple"
          onClick={() => setActiveTab('investments')}
        />
        <StatCard
          title="Total Transactions"
          value={stats.totalTransactions || 0}
          icon={DollarSign}
          color="orange"
          onClick={() => setActiveTab('transactions')}
        />
      </div>

      {/* Secondary Stats Grid - Additional Details */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <Card className="p-6">
          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <span className="text-sm text-gray-600">Investment Count</span>
              <TrendingUp className="w-4 h-4 text-purple-600" />
            </div>
            <p className="text-2xl font-bold text-gray-900">
              {stats.totalInvestmentCount || 0}
            </p>
            <div className="flex items-center space-x-2 text-xs">
              <span className="text-green-600">âœ“ Active: {stats.activeInvestments || 0}</span>
              <span className="text-gray-400">|</span>
              <span className="text-yellow-600">â³ Pending: {stats.pendingInvestments || 0}</span>
            </div>
          </div>
        </Card>

        <Card className="p-6">
          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <span className="text-sm text-gray-600">Transaction Amount</span>
              <DollarSign className="w-4 h-4 text-orange-600" />
            </div>
            <p className="text-2xl font-bold text-gray-900">
              {formatCurrency(stats.totalTransactionAmount || 0)}
            </p>
            <p className="text-xs text-gray-500">
              Total value of all transactions
            </p>
          </div>
        </Card>

        <Card className="p-6">
          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <span className="text-sm text-gray-600">Property Value</span>
              <Building2 className="w-4 h-4 text-green-600" />
            </div>
            <p className="text-2xl font-bold text-gray-900">
              {formatCurrency(stats.totalPropertyValue || 0)}
            </p>
            <p className="text-xs text-gray-500">
              Combined value of all properties
            </p>
          </div>
        </Card>
      </div>

      {/* Organization Info Card */}
      <Card className="p-6 bg-gradient-to-br from-blue-50 to-purple-50 border-blue-200">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="p-3 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full">
              <Building2 className="w-6 h-6 text-white" />
            </div>
            <div>
              <h3 className="text-lg font-semibold text-gray-900">
                Welcome to {organizationName} Dashboard
              </h3>
              <p className="text-sm text-gray-600 mt-1">
                Manage your properties, users, investments, and transactions all in one place.
              </p>
              <div className="flex items-center space-x-4 mt-2">
                <Badge variant="blue" className="text-xs">
                  Organization ID: {organizationId}
                </Badge>
                {organizationUser?.backendOrganizationName && (
                  <span className="text-xs text-gray-500">
                    Backend: {organizationUser.backendOrganizationName}
                  </span>
                )}
              </div>
            </div>
          </div>
          <div className="text-right">
            <div className="text-xs text-gray-500">Logged in as</div>
            <div className="text-sm font-medium text-gray-900">{organizationUser?.email}</div>
            <div className="text-xs text-gray-500 mt-1">
              {new Date().toLocaleDateString('en-US', { 
                weekday: 'long', 
                year: 'numeric', 
                month: 'long', 
                day: 'numeric' 
              })}
            </div>
          </div>
        </div>
      </Card>

      {/* Quick Actions */}
      <Card className="p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <Button 
            variant="outline" 
            onClick={() => setActiveTab('properties')}
            className="flex items-center justify-center space-x-2 py-4"
          >
            <Building2 className="w-5 h-5" />
            <span>View Properties</span>
          </Button>
          <Button 
            variant="outline" 
            onClick={() => setActiveTab('users')}
            className="flex items-center justify-center space-x-2 py-4"
          >
            <Users className="w-5 h-5" />
            <span>View Users</span>
          </Button>
          <Button 
            variant="outline" 
            onClick={() => setActiveTab('investments')}
            className="flex items-center justify-center space-x-2 py-4"
          >
            <TrendingUp className="w-5 h-5" />
            <span>View Investments</span>
          </Button>
          <Button 
            variant="outline" 
            onClick={() => setActiveTab('transactions')}
            className="flex items-center justify-center space-x-2 py-4"
          >
            <DollarSign className="w-5 h-5" />
            <span>View Transactions</span>
          </Button>
        </div>
      </Card>

      {/* API Status & Data Sources (Debug Info) */}
      <Card className="p-6 bg-gray-50 border-gray-200">
        <h3 className="text-sm font-semibold text-gray-700 mb-4 flex items-center">
          <span className="mr-2">ðŸ“¡</span> Data Sources & API Status
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <div className="bg-white rounded-lg p-3 border border-gray-200">
            <div className="flex items-center justify-between mb-1">
              <span className="text-xs font-medium text-gray-600">Users API</span>
              <span className={`text-xs ${Array.isArray(users) && users.length > 0 ? 'text-green-600' : 'text-gray-400'}`}>
                {Array.isArray(users) && users.length > 0 ? 'âœ“' : 'â—‹'}
              </span>
            </div>
            <div className="text-sm font-bold text-gray-900">
              {Array.isArray(users) ? users.length : 0} records
            </div>
            <div className="text-xs text-gray-500 mt-1">
              /organizations/{organizationId}/users
            </div>
          </div>

          <div className="bg-white rounded-lg p-3 border border-gray-200">
            <div className="flex items-center justify-between mb-1">
              <span className="text-xs font-medium text-gray-600">Properties API</span>
              <span className={`text-xs ${Array.isArray(properties) && properties.length > 0 ? 'text-green-600' : 'text-gray-400'}`}>
                {Array.isArray(properties) && properties.length > 0 ? 'âœ“' : 'â—‹'}
              </span>
            </div>
            <div className="text-sm font-bold text-gray-900">
              {Array.isArray(properties) ? properties.length : 0} records
            </div>
            <div className="text-xs text-gray-500 mt-1">
              /organizations/{organizationId}/properties
            </div>
          </div>

          <div className="bg-white rounded-lg p-3 border border-gray-200">
            <div className="flex items-center justify-between mb-1">
              <span className="text-xs font-medium text-gray-600">Investments API</span>
              <span className={`text-xs ${Array.isArray(investments) && investments.length > 0 ? 'text-green-600' : 'text-gray-400'}`}>
                {Array.isArray(investments) && investments.length > 0 ? 'âœ“' : 'â—‹'}
              </span>
            </div>
            <div className="text-sm font-bold text-gray-900">
              {Array.isArray(investments) ? investments.length : 0} records
            </div>
            <div className="text-xs text-gray-500 mt-1">
              /organizations/{organizationId}/investments
            </div>
          </div>

          <div className="bg-white rounded-lg p-3 border border-gray-200">
            <div className="flex items-center justify-between mb-1">
              <span className="text-xs font-medium text-gray-600">Transactions API</span>
              <span className={`text-xs ${Array.isArray(transactions) && transactions.length > 0 ? 'text-green-600' : 'text-gray-400'}`}>
                {Array.isArray(transactions) && transactions.length > 0 ? 'âœ“' : 'â—‹'}
              </span>
            </div>
            <div className="text-sm font-bold text-gray-900">
              {Array.isArray(transactions) ? transactions.length : 0} records
            </div>
            <div className="text-xs text-gray-500 mt-1">
              /organizations/{organizationId}/transactions
            </div>
          </div>
        </div>
        
        <div className="mt-4 pt-4 border-t border-gray-200">
          <div className="flex items-center justify-between text-xs">
            <span className="text-gray-600">
              <span className="font-medium">Data Source:</span> {dashboardStats ? 'Dashboard API' : 'Individual Endpoints'}
            </span>
            <span className="text-gray-600">
              <span className="font-medium">Organization:</span> {organizationId}
            </span>
            <span className="text-gray-600">
              <span className="font-medium">Last Updated:</span> {new Date().toLocaleTimeString()}
            </span>
          </div>
        </div>
      </Card>

      {dashboardLoading && (
        <Card className="p-6 text-center">
          <div className="flex items-center justify-center space-x-2">
            <div className="w-5 h-5 border-2 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
            <span className="text-gray-600">Loading dashboard data...</span>
          </div>
        </Card>
      )}
    </div>
  );

  if (!isAuthenticated) {
    return null;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center space-x-4">
              <div className="p-2 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg">
                <Building2 className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">{organizationName}</h1>
                <p className="text-sm text-gray-500">Organization Dashboard</p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <Badge variant="blue" className="text-sm px-4 py-2">
                {organizationSlug.toUpperCase()}
              </Badge>
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <Shield className="w-4 h-4" />
                <span>{organizationUser?.email}</span>
              </div>
              <Button variant="outline" onClick={handleLogout} className="flex items-center space-x-2">
                <LogOut className="w-4 h-4" />
                <span className="hidden sm:inline">Logout</span>
              </Button>
            </div>
          </div>
        </div>
      </div>

      {/* Organization Info Banner */}
      <div className="bg-gradient-to-r from-blue-500 to-purple-600 border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-3">
          <div className="flex items-center justify-between text-white">
            <div className="flex items-center space-x-3">
              <Building2 className="w-5 h-5" />
              <div>
                <p className="text-sm font-semibold">
                  ðŸ¢ {organizationName} Organization
                </p>
                <p className="text-xs opacity-90">
                  Viewing data for {organizationName} only
                </p>
              </div>
            </div>
            <Badge className="bg-white text-blue-600 font-semibold">
              {organizationSlug.toUpperCase()}
            </Badge>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Navigation Tabs */}
        <div className="mb-8 bg-white rounded-lg shadow-sm p-2 overflow-x-auto">
          <nav className="flex space-x-2 min-w-max">
            {tabs.map((tab) => {
              const Icon = tab.icon;
              return (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex items-center space-x-2 py-3 px-4 rounded-lg font-medium text-sm transition-all duration-200 whitespace-nowrap ${
                    activeTab === tab.id
                      ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-md'
                      : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
                  }`}
                >
                  <Icon className="w-4 h-4" />
                  <span>{tab.label}</span>
                </button>
              );
            })}
          </nav>
        </div>

        {/* Tab Content */}
        {renderTabContent()}
      </div>
    </div>
  );
};

export default OrgDashboard;
