import React, { createContext, useContext, useState, useEffect } from 'react';

const OrganizationContext = createContext(null);

/**
 * Organization Provider - Detects and provides organization context
 * Based on subdomain (e.g., hmr.yourdomain.com â†’ HMR organization)
 */
export const OrganizationProvider = ({ children }) => {
  const [organization, setOrganization] = useState(null);
  const [subdomain, setSubdomain] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    detectOrganization();
  }, []);

  const detectOrganization = () => {
    const hostname = window.location.hostname;
    const parts = hostname.split('.');

    // Map subdomains to organization names
    const organizationMap = {
      'hmr': 'HMR',
      'saima': 'Saima',
      'marina': 'Marina',
      // Add more organizations as needed
    };

    // Detect subdomain
    let detectedSubdomain = null;
    let detectedOrganization = null;

    if (hostname === 'localhost' || /^\d+\.\d+\.\d+\.\d+$/.test(hostname)) {
      // Localhost or IP - Admin mode
      setIsAdmin(true);
      setOrganization(null);
      setSubdomain('admin');
      console.log('ðŸ‘¨â€ðŸ’¼ Admin mode - showing all organizations');
    } else if (parts.length >= 3) {
      // Has subdomain (e.g., hmr.yourdomain.com)
      detectedSubdomain = parts[0];
      detectedOrganization = organizationMap[detectedSubdomain?.toLowerCase()];
      
      if (detectedOrganization) {
        setOrganization(detectedOrganization);
        setSubdomain(detectedSubdomain);
        setIsAdmin(false);
        console.log(`ðŸ¢ Organization: ${detectedOrganization} (${detectedSubdomain})`);
      } else {
        // Unknown subdomain - treat as admin
        setIsAdmin(true);
        setOrganization(null);
        setSubdomain('admin');
        console.log('âš ï¸ Unknown subdomain, defaulting to admin mode');
      }
    } else {
      // No subdomain - Admin mode
      setIsAdmin(true);
      setOrganization(null);
      setSubdomain('admin');
      console.log('ðŸ‘¨â€ðŸ’¼ No subdomain - admin mode');
    }

    setLoading(false);
  };

  const value = {
    organization,      // e.g., "HMR", "Saima", null
    subdomain,         // e.g., "hmr", "saima", "admin"
    isAdmin,           // true if admin mode (localhost or no subdomain)
    loading,
    
    // Helper methods
    isOrganization: (orgName) => organization === orgName,
    getOrganizationFilter: () => {
      if (isAdmin) return null; // No filter - show all
      return organization;       // Filter by organization
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading organization...</p>
        </div>
      </div>
    );
  }

  return (
    <OrganizationContext.Provider value={value}>
      {children}
    </OrganizationContext.Provider>
  );
};

/**
 * Hook to access organization context
 * @returns {{
 *   organization: string|null,
 *   subdomain: string,
 *   isAdmin: boolean,
 *   loading: boolean,
 *   isOrganization: (orgName: string) => boolean,
 *   getOrganizationFilter: () => string|null
 * }}
 */
export const useOrganization = () => {
  const context = useContext(OrganizationContext);
  if (!context) {
    throw new Error('useOrganization must be used within OrganizationProvider');
  }
  return context;
};

export default OrganizationContext;
